mapper.select:
SELECT image_type, post_id, image_url, create_at
FROM image
WHERE image_type = #{imageType}
  AND post_id = #{postId}

SELECT image_type, post_id, image_url, create_at
FROM image
WHERE image_type = #{imageType}
  AND post_id = #{postId}
ORDER BY create_at ASC
    LIMIT 1

mapper.delete:
DELETE FROM image
WHERE image_type = #{imageType}
  AND post_id = #{postId}
