mapper.update:
UPDATE badge
SET name = #{name},
    description = #{description},
    auto_grant_condition = #{autoGrantCondition}
WHERE badgeId = #{badgeId}

mapper.select:
SELECT badgeId, name, description, auto_grant_condition
FROM badge
WHERE badgeId = #{badgeId}

SELECT EXISTS(
    SELECT 1 FROM user_badge
    WHERE user_id = #{userId} AND badge_id = #{badgeId}
)

SELECT EXISTS(
    SELECT 1
    FROM project
    WHERE user_id = #{userId}
      AND progress = 'FUNDED' -- 펀딩 완료 상태
)

SELECT EXISTS(
    SELECT 1
    FROM user
    WHERE user_id = #{userId}
      AND role = 'ADMIN'
)

SELECT EXISTS(
    SELECT 1
    FROM donation
    WHERE user_id = #{userId}
)

SELECT EXISTS(
    SELECT 1
    FROM challenge_participant
    WHERE user_id = #{userId}
)

SELECT EXISTS(
    SELECT 1
    FROM user_financial_product
    WHERE user_id = #{userId}
)
