mapper.insert:
INSERT INTO challenge_log (
    user_challenge_id, user_id, log_date, verified, image_url, verified_result
)
VALUES (
           #{userChallengeId}, #{userId}, #{logDate}, #{verified}, #{imageUrl}, #{verifiedResult}
       )

mapper.select:
SELECT *
FROM challenge_log
WHERE user_challenge_id = #{userChallengeId}
  AND log_date = #{logDate}

SELECT EXISTS (
    SELECT 1
    FROM challenge_log
    WHERE user_challenge_id = #{userChallengeId}
      AND log_date = #{logDate}
)

SELECT log_id, user_challenge_id, user_id, verified, image_url, verified_result, log_date, create_at FROM challenge_log WHERE user_challenge_id = #{userChallengeId}
AND verified = #{status}
ORDER BY log_date DESC

mapper.update:
UPDATE challenge_log
SET
    verified = #{verified},
    verified_result = #{verifiedResult}
WHERE log_id = #{logId}

UPDATE user_challenge
SET current_count = current_count + 1
WHERE user_challenge_id = #{userChallengeId}

UPDATE user_challenge
SET fail_count = fail_count + 1
WHERE user_challenge_id = #{userChallengeId}
