mapper.insert:
INSERT INTO fund (
    product_id, project_id, progress, launch_at, end_at, financial_institution, upload_user_id
) VALUES (
    #{productId}, #{projectId}, #{progress}, #{launchAt}, #{endAt}, #{financialInstitution}, #{uploadUserId}
)

mapper.update:
UPDATE fund
SET progress = #{progress}
WHERE fund_id = #{fundId}

mapper.select:
SELECT COUNT(*)
FROM fund
WHERE progress = #{progress}

SELECT f.fund_id as fundId, f.product_id as productId, f.project_id as projectId, f.progress as progress, f.launch_at as launchAt, f.end_at as endAt, f.financial_institution as financialInstitution, f.retry_votes_count as retryVotesCount, fp.name as name, fp.fund_type as fundType FROM fund f INNER JOIN financial_product fp ON f.product_id = fp.product_id WHERE f.progress = #{progress}
AND fp.fund_type = #{fundType}
ORDER BY f.fund_id DESC
