mapper.select:
SELECT p.project_id, p.title, p.promotion, p.project_type, p.progress, p.create_at, p.deadline, p.user_id,
u.nickname, u.email,
b.badge_id, b.name AS badgeName FROM project p JOIN member u ON p.user_id = u.user_id LEFT JOIN user_badge ub ON u.user_id = ub.user_id LEFT JOIN badge b ON ub.badge_id = b.badge_id WHERE p.project_id = #{projectId}

SELECT COUNT(*)
FROM project
WHERE user_id = #{userId}
