mapper.select:
SELECT p.project_id, p.title, p.promotion, p.project_type, p.progress, p.create_at, p.deadline, p.user_id,
u.nickname, u.email,
b.badgeId, b.name AS badgeName FROM project p JOIN user u ON p.user_id = u.userId LEFT JOIN userbadge ub ON u.userId = ub.userId LEFT JOIN badge b ON ub.badgeId = b.badgeId WHERE p.project_id = #{projectId}

SELECT COUNT(*)
FROM project
WHERE user_id = #{userId}
