mapper.select:
SELECT user_saving_id, fund_id, user_id, saving_amount
FROM user_saving
WHERE user_saving_id = #{userSavingId}

SELECT user_saving_id, fund_id, user_id, saving_amount
FROM user_saving
WHERE user_id = #{userId}

mapper.update:
UPDATE user_saving
SET fund_id = #{fundId},
    user_id = #{userId},
    saving_amount = #{savingAmount}
WHERE user_saving_id = #{userSavingId}
